import React from 'react'
import "./Home.scss"

const card = ({img})=>(
  <img className='card' src={img} alt="cover" />
)

const Row = ({ title, arr = [] }) => (
  <div className="row">
      <h2>{title}</h2>

      <div>
          {arr.map((item, index) => (
              <Card key={index} img={`${imgUrl}/${item.poster_path}`} />
          ))}
      </div>
  </div>
);

const Home =() =>{
  return(
    <section className="home">
      <div className="banner"></div>

      <Row title ={"Popular on Netflix"}/>
    </section>
  )
}

export default Home
